mapper.select:
SELECT * FROM ( SELECT ROWNUM RNUM, QNABOARD_SEQ, USER_ID, QNABOARD_TITLE, QNABOARD_CONTENT, STATUS, REG_DATE, SECRET_FLAG, REPLY_FLAG FROM ( SELECT QNABOARD_SEQ, NVL(USER_ID,'') USER_ID, NVL(QNABOARD_TITLE,'') QNABOARD_TITLE, NVL(QNABOARD_CONTENT,'') QNABOARD_CONTENT, NVL(STATUS,'') STATUS, NVL(TO_CHAR(REG_DATE,'YYYY"년"MM"월"DD"일"'),'') REG_DATE, NVL(SECRET_FLAG,'') SECRET_FLAG, NVL(REPLY_FLAG,'') REPLY_FLAG FROM ST_QNABOARD WHERE 1 = 1
AND( QNABOARD_TITLE LIKE '%' || #{searchValue} || '%' OR DBMS_LOB.INSTR(QNABOARD_CONTENT, #{searchValue}) > 0)
ORDER BY QNABOARD_SEQ DESC ) ) WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.update:
UPDATE ST_QNA_REPLY
SET
   REPLY_CONTENT = #{replyContent}
WHERE
   QNABOARD_SEQ = #{qnaboardSeq}
