mapper.select:
SELECT USER_ID, USER_PWD, USER_EMAIL, USER_TEL, USER_POINT, USER_ADDRESS, USER_BIRTH, STATUS, REG_DATE FROM (SELECT ROWNUM RNUM, USER_ID, USER_PWD, USER_EMAIL, USER_TEL, USER_POINT, USER_ADDRESS, USER_BIRTH, STATUS, REG_DATE FROM (SELECT USER_ID, NVL(USER_PWD, '') USER_PWD, NVL(USER_NAME, '') USER_NAME, NVL(USER_EMAIL, '') USER_EMAIL, NVL(USER_TEL, '') USER_TEL, NVL(USER_POINT, 0) USER_POINT, NVL(USER_ADDRESS, '') USER_ADDRESS, NVL(USER_BIRTH, '') USER_BIRTH, NVL(STATUS, 'N') STATUS, NVL(TO_CHAR(REG_DATE, 'YYYY-MM-DD HH24:MI:SS'), '') REG_DATE FROM ST_USER
WHERE USER_ID LIKE '%' || #{searchValue} || '%'
ORDER BY USER_ID DESC)) WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.update:
UPDATE ST_REBOARD_REPLY
   SET STATUS = 'Y' 
 WHERE REPLY_SEQ = #{value}

UPDATE ST_QNABOARD
   SET REPLY_FLAG = 'Y'
 WHERE QNABOARD_SEQ = #{value}
