mapper.select:
SELECT ITEM_SEQ, ITEM_CODE, ITEM_NAME, FILE_NAME, ITEM_CATE, ITEM_PRICE, READ_CNT, STOCK, REG_DATE FROM (SELECT ROWNUM RNUM, ITEM_SEQ, FILE_NAME, ITEM_CODE, ITEM_NAME, ITEM_CATE, ITEM_PRICE, READ_CNT, STOCK, REG_DATE FROM (SELECT A.ITEM_SEQ, NVL(B.FILE_NAME, '') AS FILE_NAME, NVL(A.ITEM_CODE, '') AS ITEM_CODE, NVL(A.ITEM_NAME, '') AS ITEM_NAME, NVL(A.ITEM_CATE, '') AS ITEM_CATE, NVL(A.ITEM_PRICE, 0) AS ITEM_PRICE, NVL(A.READ_CNT, 0) AS READ_CNT, NVL(A.STOCK, 0) AS STOCK, NVL(TO_CHAR(A.REG_DATE, 'YYYY/MM/DD HH24:MI:SS'), '') AS REG_DATE FROM ITEM_DATA A, ITEM_FILE B WHERE A.ITEM_SEQ = B.ITEM_SEQ (+) AND FILE_NUM = 1
AND A.ITEM_CATE = #{itemCate}
ORDER BY A.READ_CNT DESC
ORDER BY A.REG_DATE DESC
ORDER BY A.ITEM_PRICE ASC
ORDER BY A.ITEM_PRICE DESC
)) WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.update:
UPDATE ITEM_DATA
   SET READ_CNT = READ_CNT + 1
 WHERE ITEM_SEQ = #{value}
